    def get_interaction_matrix(self):
        self.interactions_hc = np.zeros([self.nV,5,self.nBands,self.nBands])
        for _ in range(self.nV):
            self.interactions_hc[_] = self.Vsarray[_]*self.distance_mask_hc[_]
