    def get_Hoffdiag(self):
        bx, by = self.BZindices
        H = self.bare_hopping_hc*self.eikdhc[:,bx,by]
        H -= self.interactions_hc*self.eikdhc[:,bx,by]*np.conj(self.corrhc[:])
        H = np.sum(H,axis=(0,1))
